import React, { useState } from 'react';
import { Formik, Field, Form } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';

const Register = () => {
  const [upload, setUpload] = useState({
    marriageMahalProfile: null,
    businessRegistration: null,
    tradeLicense: null,
    foodLicense: null,
    fireSafetyCertificate: null,
    healthSanitationLicense: null,
    gstRegistration: null,
    liquorLicense: null,
    pestControlCertificate: null,
  });

  const initialValues = {
    email: '',
    password: '',
    confirmPassword: '',
    mahalName: '',
    mahalLocation: '',
    mahalCapacity: '',
  };

  const validationSchema = Yup.object({
    email: Yup.string().email('Invalid email address').required('Email is required'),
    password: Yup.string().min(6, 'Password must be at least 6 characters').required('Password is required'),
    confirmPassword: Yup.string()
      .oneOf([Yup.ref('password'), null], 'Passwords must match')
      .required('Confirm Password is required'),
    mahalName: Yup.string().required('Marriage Mahal Name is required'),
    mahalLocation: Yup.string().required('Marriage Mahal Location is required'),
    mahalCapacity: Yup.number().positive('Capacity must be a positive number').required('Capacity is required'),
  });

  const handleFileChange = (e, field) => {
    const file = e.target.files[0];
    setUpload((prev) => ({ ...prev, [field]: file }));
    console.log("File selected for ", field, file);
  };

  // Submit FormData correctly
  const handleSubmit = async (values, { setSubmitting, setStatus }) => {
    setSubmitting(true);
    try {
      // Upload files first
      const formData = new FormData();
      Object.keys(upload).forEach((key) => {
        if (upload[key]) {
          formData.append(key, upload[key]);
        }
      });
      const fileDataResponse = await axios.post('http://localhost:5002/api/owners/upload-files', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      const fileId = fileDataResponse.data.fileId;

      // Register marriage mahal
      const textData = {
        email,
        password,
        mahalName,
        mahalLocation,
        mahalCapacity,
        mongoObjectId: fileId, // Associate file ID with PostgreSQL entry
      };
      const textDataResponse = await axios.post('http://localhost:5000/auth/register', textData);
      if (textDataResponse.status === 200) {
        setStatus({ success: 'Registration successful!' });
      } else {
        setStatus({ error: 'Registration failed. Please try again.' });
      }
    } catch (error) {
      setStatus({ error: 'An error occurred. Please try again.' });
      console.error(error);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div>
      <h2>Register Marriage Mahal</h2>

      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={handleSubmit}
      >
        {({ values, isSubmitting, status, touched, errors }) => (
          <Form>
            {status?.error && <p style={{ color: 'red' }}>{status.error}</p>}
            {status?.success && <p style={{ color: 'green' }}>{status.success}</p>}

            {/* Owner Information */}
            <div>
              <h3>Owner Information</h3>
              <div>
                <input
                  name="email"
                  type="email"
                  placeholder="Owner Email"
                  value={values.email}
                  onChange={(e) => values.email = e.target.value}
                />
                {touched.email && errors.email && (
                  <div style={{ color: 'red' }}>{errors.email}</div>
                )}
              </div>
              <div>
                <input
                  name="password"
                  type="password"
                  placeholder="Password"
                  value={values.password}
                  onChange={(e) => values.password = e.target.value}
                />
                {touched.password && errors.password && (
                  <div style={{ color: 'red' }}>{errors.password}</div>
                )}
              </div>
              <div>
                <input
                  name="confirmPassword"
                  type="password"
                  placeholder="Confirm Password"
                  value={values.confirmPassword}
                  onChange={(e) => values.confirmPassword = e.target.value}
                />
                {touched.confirmPassword && errors.confirmPassword && (
                  <div style={{ color: 'red' }}>{errors.confirmPassword}</div>
                )}
              </div>
            </div>

            {/* Marriage Mahal Information */}
            <div>
              <div>
                <input
                  name="mahalName"
                  type="text"
                  placeholder="Marriage Mahal Name"
                  value={values.mahalName}
                  onChange={(e) => values.mahalName = e.target.value}
                />
                {touched.mahalName && errors.mahalName && (
                  <div style={{ color: 'red' }}>{errors.mahalName}</div>
                )}
              </div>
              <div>
                <input
                  name="mahalLocation"
                  type="text"
                  placeholder="Marriage Mahal Location"
                  value={values.mahalLocation}
                  onChange={(e) => values.mahalLocation = e.target.value}
                />
                {touched.mahalLocation && errors.mahalLocation && (
                  <div style={{ color: 'red' }}>{errors.mahalLocation}</div>
                )}
              </div>
              <div>
                <input
                  name="mahalCapacity"
                  type="number"
                  placeholder="Marriage Mahal Capacity"
                  value={values.mahalCapacity}
                  onChange={(e) => values.mahalCapacity = e.target.value}
                />
                {touched.mahalCapacity && errors.mahalCapacity && (
                  <div style={{ color: 'red' }}>{errors.mahalCapacity}</div>
                )}
              </div>

              {/* File Upload for Marriage Mahal Profile */}
              <div>
                <label>Marriage Mahal Profile:</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'marriageMahalProfile')}
                />
                {upload.marriageMahalProfile && (
                  <p>File selected: {upload.marriageMahalProfile.name}</p>
                )}
              </div>

              {/* File Upload for required certificates (excluding businessRegistration) */}
              {[
                'tradeLicense',
                'foodLicense',
                'fireSafetyCertificate',
                'healthSanitationLicense',
                'gstRegistration',
                'liquorLicense',
                'pestControlCertificate',
              ].map((field) => (
                <div key={field}>
                  <label>{field.replace(/([A-Z])/g, ' $1').toUpperCase()}:</label>
                  <input
                    type="file"
                    onChange={(e) => handleFileChange(e, field)}
                  />
                </div>
              ))}

              <button type="submit" disabled={isSubmitting}>
                Submit
              </button>
            </div>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default Register;
