import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Settings.css';

const Settings = () => {
  const [userDetails, setUserDetails] = useState({
    name: '',
    password: '',
    email: '', // Email fetched from sessionStorage
  });

  useEffect(() => {
    // Fetch username and email from sessionStorage
    const storedUsername = sessionStorage.getItem('username');
    const storedEmail = sessionStorage.getItem('email');

    if (storedUsername && storedEmail) {
      setUserDetails((prevDetails) => ({
        ...prevDetails,
        name: storedUsername,
        email: storedEmail,
      }));
    } else {
      // If not in sessionStorage, fetch from backend
      const fetchUserDetails = async () => {
        try {
          axios.put('http://localhost:5000/user/update', { name, password }, {
            headers: { Authorization: `Bearer ${token}` }
          });

          setUserDetails((prevDetails) => ({
            ...prevDetails,
            name,
            email,
          }));
        } catch (error) {
          console.error('Error fetching user details:', error);
          alert('Failed to fetch user details. Please try again.');
        }
      };

      fetchUserDetails();
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  const handleSave = async () => {
    try {
      const { name, password } = userDetails; // Only send name and password for updates
      const response = await axios.put('http://localhost:5000/user/update', { name, password });
      alert('Settings saved successfully!');
      console.log('Response:', response.data);
    } catch (error) {
      console.error('Error updating user details:', error);
      alert('Failed to save settings. Please try again.');
    }
  };

  return (
    <div className="settings-page">
      <h2>Account Settings</h2>
      <form className="settings-form">
        <div className="form-group">
          <label htmlFor="name">Full Name</label>
          <input
            type="text"
            id="name"
            name="name"

            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={userDetails.email}
            disabled // Disable the email field
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={userDetails.password}
            placeholder="Enter new password"
            onChange={handleChange}
          />
        </div>

        <button type="button" className="save-button" onClick={handleSave}>
          Save Changes
        </button>
      </form>
    </div>
  );
};

export default Settings;
