import React, { useState } from 'react';
import axios from 'axios';
import './CreateBooking.css';

const auspiciousTimes = {
  '2024-12-20': ['9:00 AM - 10:30 AM', '2:00 PM - 3:30 PM'],
  '2024-12-21': ['8:30 AM - 10:00 AM', '4:00 PM - 5:30 PM'],
  '2024-12-22': ['10:00 AM - 11:30 AM', '3:00 PM - 4:30 PM'],
};

const CreateBooking = () => {
  const [formData, setFormData] = useState({
    customerName: '',
    venue: '',
    eventDate: '',
    eventType: '',
    numberOfGuests: '',
    auspiciousTime: '',
  });

  const [message, setMessage] = useState('');
  const [availableTimes, setAvailableTimes] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormData({
      ...formData,
      [name]: value,
    });

    if (name === 'eventDate') {
      // Update available auspicious times based on the selected date
      setAvailableTimes(auspiciousTimes[value] || []);
      setFormData((prev) => ({ ...prev, auspiciousTime: '' })); // Reset time if date changes
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/bookings', formData);
      setMessage(response.data.message);

      setFormData({
        customerName: '',
        venue: '',
        eventDate: '',
        eventType: '',
        numberOfGuests: '',
        auspiciousTime: '',
      });
    } catch (error) {
      setMessage('Failed to create booking.');
    }
  };

  return (
    <div className="create-booking">
      <h2>Create New Booking</h2>
      <form onSubmit={handleSubmit} className="booking-form">
        {/* Existing Form Fields */}
        <div className="form-group">
          <label htmlFor="customerName">Customer Name</label>
          <input
            type="text"
            id="customerName"
            name="customerName"
            value={formData.customerName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="venue">Venue</label>
          <select
            id="venue"
            name="venue"
            value={formData.venue}
            onChange={handleChange}
            required
          >
            <option value="">Select Venue</option>
            <option value="Venue A">Venue A</option>
            <option value="Venue B">Venue B</option>
            <option value="Venue C">Venue C</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="eventDate">Event Date</label>
          <input
            type="date"
            id="eventDate"
            name="eventDate"
            value={formData.eventDate}
            onChange={handleChange}
            min={new Date().toISOString().split('T')[0]}
            required
          />
        </div>

        {/* New Auspicious Time Dropdown */}
        <div className="form-group">
          <label htmlFor="auspiciousTime">Auspicious Time</label>
          <select
            id="auspiciousTime"
            name="auspiciousTime"
            value={formData.auspiciousTime}
            onChange={handleChange}
            required
            disabled={!availableTimes.length}
          >
            <option value="">Select Auspicious Time</option>
            {availableTimes.map((time, index) => (
              <option key={index} value={time}>
                {time}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="numberOfGuests">Number of Guests</label>
          <input
            type="number"
            id="numberOfGuests"
            name="numberOfGuests"
            value={formData.numberOfGuests}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit" className="submit-btn1">Create Booking</button>
      </form>

      {message && <div className="message">{message}</div>}
    </div>
  );
};

export default CreateBooking;
