import React, { useState } from "react";
import "./LoginPage.css";

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(""); // To store error message
  const [loading, setLoading] = useState(false); // To manage loading state

  const handleLogin = async (e) => {
    e.preventDefault();

    // Reset error and loading state
    setError("");
    setLoading(true);

    // Validate input fields
    if (!email || !password) {
      setError("Please fill in all fields");
      setLoading(false);
      return;
    }

    try {
      // Make an API call to your backend server for login (using localhost)
      const response = await fetch("http://localhost:5000/login", { // Local server endpoint
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
      });

      // Check if the response is successful
      if (response.ok) {
        const data = await response.json();
        console.log("Login successful", data);
        // Handle successful login, e.g., redirect or store token
        // For example, you can store the token in localStorage:
        // localStorage.setItem("token", data.token);
        // Redirect after successful login (optional)
        window.location.href = "/dashboard"; // Modify this to your desired page
      } else {
        const errorData = await response.json();
        setError(errorData.message || "Login failed, please try again");
      }
    } catch (error) {
      setError("An error occurred while logging in. Please try again.");
      console.error(error);
    } finally {
      setLoading(false); // Reset loading state after API call
    }
  };

  return (
    <div className="auth-container">
      <form onSubmit={handleLogin} className="auth-form">
        <h2>Login</h2>

        {error && <p className="error-message">{error}</p>} {/* Display error message */}

        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Enter your email"
          required
        />

        <label>Password:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Enter your password"
          required
        />

        <button type="submits1" disabled={loading}>
          {loading ? "Logging in..." : "Login"}
        </button>

        <p>
          Don't have an account? <a href="/signup">Sign up</a>
        </p>
      </form>
    </div>
  );
};

export default LoginPage;
