import React, { useState } from 'react';
import { Formik, Field, Form } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';

// Custom input component
const InputField = ({ field, form, ...props }) => {
  return (
    <div>
      <input {...field} {...props} />
      {form.touched[field.name] && form.errors[field.name] && (
        <div style={{ color: 'red' }}>{form.errors[field.name]}</div>
      )}
    </div>
  );
};

const Register = () => {
  const [upload, setUpload] = useState({
    marriageMahalProfile: null,
    businessRegistration: null,
    tradeLicense: null,
    foodLicense: null,
    fireSafetyCertificate: null,
    healthSanitationLicense: null,
    gstRegistration: null,
    liquorLicense: null,
    pestControlCertificate: null,
  });

  const initialValues = {
    email: '',
    password: '',
    confirmPassword: '',
    mahalName: '',
    mahalLocation: '',
    mahalCapacity: '',
  };
  const [files, setFiles] = useState(null);


  const validationSchema = Yup.object({
    email: Yup.string().email('Invalid email address').required('Email is required'),
    password: Yup.string().min(6, 'Password must be at least 6 characters').required('Password is required'),
    confirmPassword: Yup.string()
      .oneOf([Yup.ref('password'), null], 'Passwords must match')
      .required('Confirm Password is required'),
    mahalName: Yup.string().required('Marriage Mahal Name is required'),
    mahalLocation: Yup.string().required('Marriage Mahal Location is required'),
    mahalCapacity: Yup.number().positive('Capacity must be a positive number').required('Capacity is required'),
  });

  const handleFileChange = (e, field) => {
    const file = e.target.files[0];
    setUpload((prev) => ({ ...prev, [field]: file }));
  };

  const handleSubmit = async (values, { setSubmitting, setStatus }) => {
    try {
      // Prepare file upload
      const formData = new FormData();
      formData.append('file', fileInput.files[0]); // append your file(s) here

      axios.post('http://localhost:5002/api/owners/upload-files', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      })
      .then(response => {
        console.log(response.data);
      })
      .catch(error => {
        console.error(error.response.data);
      });

      if (fileUploadResponse.status === 200) {
        console.log('Files uploaded successfully.');

        // Prepare text data
        const textData = {
          email: values.email,
          mahalName: values.mahalName,
          mahalLocation: values.mahalLocation,
          mahalCapacity: values.mahalCapacity,
          fileId: fileUploadResponse.data.fileId,
        };

        // Submit text data
        const textDataResponse = await axios.post('http://localhost:5000/auth/register', textData);
        if (textDataResponse.status === 200) {
          setStatus({ success: 'Marriage Mahal registered successfully.' });
          console.log('Text data submitted successfully.');
        } else {
          setStatus({ error: 'Text data submission failed.' });
        }
      } else {
        setStatus({ error: 'File upload failed.' });
      }
    } catch (error) {
      setStatus({ error: 'An error occurred during submission.' });
      console.error('Error:', error);
    } finally {
      setSubmitting(false);
    }
  };


  return (
    <div>
      <h2>Register Marriage Mahal</h2>

      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={handleSubmit}
      >
        {({ values, isSubmitting, status }) => (
          <Form>
            {status?.error && <p style={{ color: 'red' }}>{status.error}</p>}
            {status?.success && <p style={{ color: 'green' }}>{status.success}</p>}

            {/* Owner Information */}
            <div>
              <h3>Owner Information</h3>
              <Field
                name="email"
                type="email"
                placeholder="Owner Email"
                component={InputField}
              />
              <Field
                name="password"
                type="password"
                placeholder="Password"
                component={InputField}
              />
              <Field
                name="confirmPassword"
                type="password"
                placeholder="Confirm Password"
                component={InputField}
              />
            </div>

            {/* Marriage Mahal Information */}
            <div>
              <Field
                name="mahalName"
                type="text"
                placeholder="Marriage Mahal Name"
                component={InputField}
              />
              <Field
                name="mahalLocation"
                type="text"
                placeholder="Marriage Mahal Location"
                component={InputField}
              />
              <Field
                name="mahalCapacity"
                type="number"
                placeholder="Marriage Mahal Capacity"
                component={InputField}
              />

              {/* File Upload for Marriage Mahal Profile */}
              <div>
                <label>Marriage Mahal Profile:</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'marriageMahalProfile')}
                />
                {upload.marriageMahalProfile && (
                  <p>File selected: {upload.marriageMahalProfile.name}</p>
                )}
              </div>

              {/* File Upload for required certificates */}
              {[
                'businessRegistration',
                'tradeLicense',
                'foodLicense',
                'fireSafetyCertificate',
                'healthSanitationLicense',
                'gstRegistration',
                'liquorLicense',
                'pestControlCertificate',
              ].map((field) => (
                <div key={field}>
                  <label>{field.replace(/([A-Z])/g, ' $1').toUpperCase()}:</label>
                  <input
                    type="file"
                    onChange={(e) => handleFileChange(e, field)}
                  />
                </div>
              ))}

              <button type="submit" disabled={isSubmitting}>
                Submit
              </button>
            </div>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default Register;
