import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

const DashboardActions = () => {
  const navigate = useNavigate();

  // Retrieve username and email from sessionStorage
  const username = sessionStorage.getItem('username');
  const email = sessionStorage.getItem('email');

  console.log("Username:", sessionStorage.getItem("username"));
console.log("Email:", sessionStorage.getItem("email"));


  const handleCreateBooking = () => {
    navigate('/createbooking'); // Replace '/createbooking' with your route
  };

  const handleViewBooking = () => {
    navigate('/viewbooking'); // Replace '/viewbooking' with your route
  };

  const handleSettings = () => {
    navigate('/settings'); // Replace '/settings' with your route
  };

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>Welcome to {name} Dashboard</h1>
        <p>Manage your bookings, venues, and account settings.</p>
        {/* Display username and email */}

      </header>

      <section className="dashboard-stats">
        <div className="stats-card">
          <h3>Total Bookings</h3>
          {/* Add dynamic content or a placeholder */}
        </div>
        <div className="stats-card">
          <h3>Pending Approvals</h3>
          {/* Add dynamic content or a placeholder */}
        </div>
        <div className="stats-card">
          <h3>Upcoming Events</h3>
          {/* Add dynamic content or a placeholder */}
        </div>
      </section>

      <section className="dashboard-actions">
        <div className="action-card">
          <h3>Create New Booking</h3>
          <p>Create and manage new bookings for venues.</p>
          <button onClick={handleCreateBooking}>Create Booking</button>
        </div>
        <div className="action-card">
          <h3>View Bookings</h3>
          <p>View and edit all your previous and current bookings.</p>
          <button onClick={handleViewBooking}>View Bookings</button>
        </div>
        <div className="action-card">
          <h3>Account Settings</h3>
          <p>Update your profile, preferences, and account details.</p>
          <button onClick={handleSettings}>Settings</button>
        </div>
      </section>
    </div>
  );
};

export default DashboardActions;
