import React, { useState, useEffect } from 'react';

const AccountSettings = () => {
  const [profile, setProfile] = useState({
    username: '',
    email: '',
  });

  // Fetch data from sessionStorage on component mount
  useEffect(() => {
    const storedUsername = sessionStorage.getItem('username');
    const storedEmail = sessionStorage.getItem('email');

    if (storedUsername && storedEmail) {
      setProfile({
        username: storedUsername,
        email: storedEmail,
      });
    }
  }, []);

  // Handle form submission (example for updating profile)
  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle the form submission, like sending data to a backend
    console.log('Profile updated:', profile);
  };

  // Handle input changes for updating the profile (if required)
  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setProfile((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div className="account-settings">
      <h1>Account Settings</h1>

      <form onSubmit={handleSubmit}>
        <div className="profile-section">
          <h2>Profile</h2>

          <label>
            Username:
            <input
              type="text"
              name="username"
              value={profile.username}
              onChange={handleProfileChange}
              placeholder="Enter your username"
            />
          </label>

          <label>
            Email:
            <input
              type="email"
              name="email"
              value={profile.email}
              onChange={handleProfileChange}
              placeholder="Enter your email"
            />
          </label>
        </div>

        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
};

export default AccountSettings;
