import React, { useState } from "react";
import axios from "axios";
import "../App.css";

const SignupPage = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    mobile: "",
    password: "",
    confirmPassword: "",
  });
  const [errors, setErrors] = useState({});
  const [passwordStrength, setPasswordStrength] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const [otp, setOtp] = useState("");

  // Validation helpers
  const validateField = (field, value) => {
    switch (field) {
      case "name":
        return value.trim().length > 2
          ? ""
          : "Name must be at least 3 characters long.";
      case "email":
        return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value)
          ? ""
          : "Invalid email format.";
      case "mobile":
        return /^[6-9]\d{9}$/.test(value)
          ? ""
          : "Invalid mobile number. Must be 10 digits and start with 6-9.";
      case "password":
        checkPasswordStrength(value);
        return value.length >= 8
          ? ""
          : "Password must be at least 8 characters long.";
      case "confirmPassword":
        return value === formData.password
          ? ""
          : "Passwords do not match.";
      default:
        return "";
    }
  };

  const checkPasswordStrength = (password) => {
    if (password.length >= 8 && /[A-Z]/.test(password) && /[0-9]/.test(password)) {
      setPasswordStrength("Strong");
    } else if (password.length >= 6) {
      setPasswordStrength("Medium");
    } else {
      setPasswordStrength("Weak");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
    setErrors((prev) => ({ ...prev, [name]: validateField(name, value) }));
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrors({});

    // Final validation
    const newErrors = Object.keys(formData).reduce((acc, field) => {
      const error = validateField(field, formData[field]);
      if (error) acc[field] = error;
      return acc;
    }, {});

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      setLoading(false);
      return;
    }

    // API call for signup
    try {
      await axios.post("http://localhost:5000/auth/signup", formData);
      // Trigger OTP generation
      await handleGenerateOtp();
    } catch (error) {
      setErrors({ form: error.response?.data?.message || "An error occurred." });
    } finally {
      setLoading(false);
    }
  };

  const handleGenerateOtp = async () => {
    try {
      const response = await axios.post("http://localhost:5000/auth/send-otp", {
        mobile: formData.mobile,
      });
      setOtpSent(true);
      setSuccess(response.data.message || "OTP sent successfully!");
    } catch (error) {
      setErrors({ otp: error.response?.data?.message || "Failed to send OTP." });
    }
  };

  const handleVerifyOtp = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post("http://localhost:5000/auth/verify-otp", {
        mobile: formData.mobile,
        otp,
      });
      setSuccess(response.data.message || "Signup and OTP verification successful!");
      setOtpSent(false); // Reset OTP flow
    } catch (error) {
      setErrors({ otp: error.response?.data?.message || "Invalid OTP." });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      {!otpSent ? (
        <form onSubmit={handleSignup} className="auth-form">
          <h2>Create an Account</h2>

          {errors.form && <p className="error-message">{errors.form}</p>}
          {success && <p className="success-message">{success}</p>}

          {/* Name Field */}
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Enter your full name"
              className={errors.name ? "invalid" : "valid"}
            />
            {errors.name && <small className="error">{errors.name}</small>}
          </div>

          {/* Email Field */}
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter your email"
              className={errors.email ? "invalid" : "valid"}
            />
            {errors.email && <small className="error">{errors.email}</small>}
          </div>

          {/* Mobile Field */}
          <div className="form-group">
            <label>Mobile Number:</label>
            <input
              type="tel"
              name="mobile"
              value={formData.mobile}
              onChange={handleChange}
              placeholder="Enter your mobile number"
              className={errors.mobile ? "invalid" : "valid"}
            />
            {errors.mobile && <small className="error">{errors.mobile}</small>}
          </div>

          {/* Password Field */}
          <div className="form-group">
            <label>Password:</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Create a strong password"
              className={errors.password ? "invalid" : "valid"}
            />
            {passwordStrength && (
              <small className={`password-strength ${passwordStrength.toLowerCase()}`}>
                Strength: {passwordStrength}
              </small>
            )}
            {errors.password && <small className="error">{errors.password}</small>}
          </div>

          {/* Confirm Password Field */}
          <div className="form-group">
            <label>Confirm Password:</label>
            <input
              type="password"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              placeholder="Re-enter your password"
              className={errors.confirmPassword ? "invalid" : "valid"}
            />
            {errors.confirmPassword && (
              <small className="error">{errors.confirmPassword}</small>
            )}
          </div>

          <button type="submit" disabled={loading}>
            {loading ? "Processing..." : "Sign Up"}
          </button>
        </form>
      ) : (
        <form onSubmit={handleVerifyOtp} className="auth-form">
          <h2>Verify OTP</h2>

          {errors.otp && <p className="error-message">{errors.otp}</p>}
          {success && <p className="success-message">{success}</p>}

          {/* OTP Field */}
          <div className="form-group">
            <label>Enter OTP:</label>
            <input
              type="text"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              placeholder="Enter the OTP sent to your mobile"
            />
          </div>

          <button type="submit" disabled={loading}>
            {loading ? "Verifying..." : "Verify OTP"}
          </button>
        </form>
      )}
    </div>
  );
};

export default SignupPage;
