import React, { useState } from "react";
import axios from "axios";
import "./CreateBooking.css";

const AdvancedBookingForm = () => {
  const [formData, setFormData] = useState({
    customerName: "",
    email: "",
    phone: "",
    venue: "",
    eventDate: "",
    eventType: "",
    numberOfGuests: "",
    specialRequests: "",
    guestDetails: [{ name: "", preference: "" }],
    attachment: null,
  });

  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    const { name, value, type, files } = e.target;
    if (type === "file") {
      setFormData({ ...formData, attachment: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleGuestChange = (index, e) => {
    const updatedGuests = [...formData.guestDetails];
    updatedGuests[index][e.target.name] = e.target.value;
    setFormData({ ...formData, guestDetails: updatedGuests });
  };

  const addGuest = () => {
    setFormData({
      ...formData,
      guestDetails: [...formData.guestDetails, { name: "", preference: "" }],
    });
  };

  const removeGuest = (index) => {
    const updatedGuests = formData.guestDetails.filter((_, i) => i !== index);
    setFormData({ ...formData, guestDetails: updatedGuests });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formDataToSend = new FormData();
    Object.entries(formData).forEach(([key, value]) => {
      if (key === "guestDetails") {
        formDataToSend.append(key, JSON.stringify(value));
      } else {
        formDataToSend.append(key, value);
      }
    });

    try {
      const response = await axios.post(
        "http://localhost:5000/api/bookings",
        formDataToSend
      );
      setMessage(response.data.message);
      setFormData({
        customerName: "",
        email: "",
        phone: "",
        venue: "",
        eventDate: "",
        eventType: "",
        numberOfGuests: "",
        specialRequests: "",
        guestDetails: [{ name: "", preference: "" }],
        attachment: null,
      });
    } catch (error) {
      setMessage("Failed to create booking.");
    }
  };

  return (
    <div className="create-booking">
      <h2>Mahal Booking</h2>
      <form onSubmit={handleSubmit} className="booking-form" encType="multipart/form-data">
        <div className="form-group">
          <label htmlFor="customerName">Customer Name</label>
          <input
            type="text"
            id="customerName"
            name="customerName"
            value={formData.customerName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="phone">Phone</label>
          <input
            type="tel"
            id="phone"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="venue">Venue</label>
          <select
            id="venue"
            name="venue"
            value={formData.venue}
            onChange={handleChange}
            required
          >
            <option value="">Select Venue</option>
            <option value="Venue A">Venue A</option>
            <option value="Venue B">Venue B</option>
            <option value="Venue C">Venue C</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="eventDate">Event Date</label>
          <input
            type="date"
            id="eventDate"
            name="eventDate"
            value={formData.eventDate}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="eventType">Event Type</label>
          <select
            id="eventType"
            name="eventType"
            value={formData.eventType}
            onChange={handleChange}
            required
          >
            <option value="">Select Event Type</option>
            <option value="Wedding">Wedding</option>
            <option value="Conference">Conference</option>
            <option value="Birthday">Birthday</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="numberOfGuests">Number of Guests</label>
          <input
            type="number"
            id="numberOfGuests"
            name="numberOfGuests"
            value={formData.numberOfGuests}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="specialRequests">Special Requests</label>
          <textarea
            id="specialRequests"
            name="specialRequests"
            value={formData.specialRequests}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label>Guest Details</label>
          {formData.guestDetails.map((guest, index) => (
            <div key={index} className="guest-fields">
              <input
                type="text"
                name="name"
                placeholder="Guest Name"
                value={guest.name}
                onChange={(e) => handleGuestChange(index, e)}
                required
              />
              <input
                type="text"
                name="preference"
                placeholder="Dietary Preference"
                value={guest.preference}
                onChange={(e) => handleGuestChange(index, e)}
              />
              {index > 0 && (
                <button type="button" onClick={() => removeGuest(index)}>
                  Remove
                </button>
              )}
            </div>
          ))}
          <button type="button" onClick={addGuest}>
            Add Guest
          </button>
        </div>

        <div className="form-group">
          <label htmlFor="attachment">Upload File</label>
          <input
            type="file"
            id="attachment"
            name="attachment"
            onChange={handleChange}
            accept=".pdf,.jpg,.png"
          />
        </div>

        <button type="submit" className="submit-btn">Create Booking</button>
      </form>

      {message && <div className="message">{message}</div>}
    </div>
  );
};

export default AdvancedBookingForm;
