import React, { useState } from 'react';
import './CreateBooking.css';

const CreateBooking = () => {
  const [formData, setFormData] = useState({
    customerName: '',
    venue: '',
    eventDate: '',
    eventType: '',
    numberOfGuests: '',
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Simulate a successful form submission
    setMessage('Booking created successfully!');

    // You can make a POST request to the backend API to save this data
    // Example:
    // axios.post('/api/bookings', formData)
    //   .then(response => {
    //     setMessage('Booking created successfully!');
    //   })
    //   .catch(error => {
    //     setMessage('Failed to create booking.');
    //   });

    setFormData({
      customerName: '',
      venue: '',
      eventDate: '',
      eventType: '',
      numberOfGuests: '',
    });
  };

  return (
    <div className="create-booking">
      <h2>Create New Booking</h2>
      <form onSubmit={handleSubmit} className="booking-form">
        <div className="form-group">
          <label htmlFor="customerName">Customer Name</label>
          <input
            type="text"
            id="customerName"
            name="customerName"
            value={formData.customerName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="venue">Venue</label>
          <select
            id="venue"
            name="venue"
            value={formData.venue}
            onChange={handleChange}
            required
          >
            <option value="">Select Venue</option>
            <option value="Venue A">Venue A</option>
            <option value="Venue B">Venue B</option>
            <option value="Venue C">Venue C</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="eventDate">Event Date</label>
          <input
            type="date"
            id="eventDate"
            name="eventDate"
            value={formData.eventDate}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="eventType">Event Type</label>
          <select
            id="eventType"
            name="eventType"
            value={formData.eventType}
            onChange={handleChange}
            required
          >
            <option value="">Select Event Type</option>
            <option value="Wedding">Wedding</option>
            <option value="Conference">Conference</option>
            <option value="Birthday">Birthday</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="numberOfGuests">Number of Guests</label>
          <input
            type="number"
            id="numberOfGuests"
            name="numberOfGuests"
            value={formData.numberOfGuests}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit" className="submit-btn">Create Booking</button>
      </form>

      {message && <div className="message">{message}</div>}
    </div>
  );
};

export default CreateBooking;
