import React, { useState } from 'react';
import { Formik, Field, Form } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';

// Custom input component
const InputField = ({ field, form, ...props }) => {
  return (
    <div>
      <input {...field} {...props} />
      {form.touched[field.name] && form.errors[field.name] && (
        <div style={{ color: 'red' }}>{form.errors[field.name]}</div>
      )}
    </div>
  );
};

const Register = () => {
  const [upload, setUpload] = useState({
    businessRegistration: null,
    tradeLicense: null,
    foodLicense: null,
    fireSafetyCertificate: null,
    healthSanitationLicense: null,
    gstRegistration: null,
    liquorLicense: null,
    pestControlCertificate: null,
  });

  // Initial form values (now including files and profile)
  const initialValues = {
    mahalName: '',
    mahalLocation: '',
    mahalCapacity: '',
    mahalProfile: '',  // New field for the Marriage Mahal profile
    profilePicture: null, // Optional: Still allowing file upload if needed
  };

  // Validation schema (including profile validation)
  const validationSchema = Yup.object({
    mahalName: Yup.string().required('Marriage Mahal Name is required'),
    mahalLocation: Yup.string().required('Marriage Mahal Location is required'),
    mahalCapacity: Yup.number()
      .positive('Capacity must be a positive number')
      .required('Capacity is required'),
    mahalProfile: Yup.string().required('Marriage Mahal Profile is required'),  // Validate profile field
  });

  // Submit handler
  const handleSubmit = async (values, { setSubmitting, setStatus }) => {
    const formData = new FormData();

    // Add form values to formData
    for (let key in values) {
      formData.append(key, values[key]);
    }

    // Add files to formData
    for (let key in upload) {
      if (upload[key]) {
        formData.append(key, upload[key]);
      }
    }

    try {
      const response = await axios.post('http://localhost:5000/api/owners/register', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setStatus({ success: 'Registration successful. You can now log in!' });
    } catch (error) {
      const message = error.response?.data?.error || 'Registration failed. Please try again.';
      setStatus({ error: message });
    }
    setSubmitting(false);
  };

  // Handle file upload change
  const handleFileChange = (e, field) => {
    const file = e.target.files[0];
    setUpload((prev) => ({ ...prev, [field]: file }));
  };

  return (
    <div>
      <h2>Register Marriage Mahal</h2>

      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={handleSubmit}
      >
        {({ values, isSubmitting, status }) => (
          <Form>
            {status?.error && <p style={{ color: 'red' }}>{status.error}</p>}
            {status?.success && <p style={{ color: 'green' }}>{status.success}</p>}

            {/* Marriage Mahal Information */}
            <div>
              <h3>Marriage Mahal Information</h3>
              <Field
                name="mahalName"
                type="text"
                placeholder="Marriage Mahal Name"
                component={InputField}
              />
              <Field
                name="mahalLocation"
                type="text"
                placeholder="Marriage Mahal Location"
                component={InputField}
              />
              <Field
                name="mahalCapacity"
                type="number"
                placeholder="Marriage Mahal Capacity"
                component={InputField}
              />

              {/* New Marriage Mahal Profile (Description) */}
              <div>
                <label>Marriage Mahal Profile (Description):</label>
                <textarea
                  name="mahalProfile"
                  placeholder="Enter a brief description of the Marriage Mahal"
                  rows="5"
                  onChange={e => values.mahalProfile = e.target.value} 
                  value={values.mahalProfile}
                />
                {status?.error && <div style={{ color: 'red' }}>{status?.error}</div>}
              </div>

              {/* File Upload for required certificates */}
              <div>
                <label>Business Registration:</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'businessRegistration')}
                />
              </div>
              <div>
                <label>Trade License:</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'tradeLicense')}
                />
              </div>
              <div>
                <label>Food License (if applicable):</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'foodLicense')}
                />
              </div>
              <div>
                <label>Fire Safety Certificate:</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'fireSafetyCertificate')}
                />
              </div>
              <div>
                <label>Health and Sanitation License:</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'healthSanitationLicense')}
                />
              </div>
              <div>
                <label>GST Registration:</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'gstRegistration')}
                />
              </div>
              <div>
                <label>Liquor License (if applicable):</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'liquorLicense')}
                />
              </div>
              <div>
                <label>Pest Control Certification:</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'pestControlCertificate')}
                />
              </div>

              <button type="submit" disabled={isSubmitting}>
                Submit
              </button>
            </div>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default Register;
