import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Settings.css';
import PasswordStrengthBar from 'react-password-strength-bar';

const AccountSettings = () => {
  const [profile, setProfile] = useState({
    name: '',
    email: '',
    password: '',
    profilePic: '',
  });
  const [preferences, setPreferences] = useState({
    theme: 'light',
    notifications: true,
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [message, setMessage] = useState('');
  const [passwordStrength, setPasswordStrength] = useState(0); 

  useEffect(() => {
    const storedUsername = sessionStorage.getItem('username');
    const storedEmail = sessionStorage.getItem('email');

    if (storedUsername && storedEmail) {
      setProfile({
        name: storedUsername,
        email: storedEmail,
      });
    }
  }, []);

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/user/profile'); // Assuming a backend endpoint
        setProfile(response.data.profile);
        setPreferences(response.data.preferences);
      } catch (error) {
        console.error('Error fetching profile data', error);
      }
    };
    fetchProfileData();
  }, []);

  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setProfile((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handlePreferencesChange = (e) => {
    const { name, value, type, checked } = e.target;
    setPreferences((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handlePasswordChange = (e) => {
    const { value } = e.target;
    setProfile((prev) => ({
      ...prev,
      password: value,
    }));
    setPasswordStrength(getPasswordStrength(value)); 
  };

  const handleProfilePicChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setProfile((prev) => ({
          ...prev,
          profilePic: reader.result,
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  const getPasswordStrength = (password) => {
    let strength = 0;
    if (password.length >= 6) strength++;
    if (/[A-Z]/.test(password)) strength++;
    if (/[0-9]/.test(password)) strength++;
    if (/[^A-Za-z0-9]/.test(password)) strength++;
    return strength;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setMessage('');

    try {
      const formData = new FormData();
      formData.append('profile', JSON.stringify(profile)); // Send profile data
      formData.append('preferences', JSON.stringify(preferences)); // Send preferences

      await axios.post('http://localhost:5000/api/user/update', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      setMessage('Profile updated successfully!');
    } catch (error) {
      console.error('Error updating profile:', error);
      setMessage('Error updating profile.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="account-settings">
      <h1>Account Settings</h1>
      {message && <div className="message">{message}</div>}

      <form onSubmit={handleSubmit}>
        <div className="profile-section">
          <h2>Profile</h2>
          <label>
            Name:
            <input
              type="text"
              name="name"
              value={profile.name}
              onChange={handleProfileChange}
              placeholder="Enter your name"
            />
          </label>

          <label>
            Email:
            <input
              type="email"
              name="email"
              value={profile.email}
              onChange={handleProfileChange}
              placeholder="Enter your email"
              disabled
            />
          </label>

          <label>
            Password:
            <input
              type="password"
              name="password"
              value={profile.password}
              onChange={handlePasswordChange}
              placeholder="Enter your password"
            />
            <PasswordStrengthBar password={profile.password} />
          </label>

          <label>
            Profile Picture:
            <input
              type="file"
              name="profilePic"
              onChange={handleProfilePicChange}
            />
            {profile.profilePic && (
              <img
                src={profile.profilePic}
                alt="Profile Preview"
                className="profile-preview"
              />
            )}
          </label>
        </div>

        <div className="preferences-section">
          <h2>Preferences</h2>
          <label>
            Theme:
            <select
              name="theme"
              value={preferences.theme}
              onChange={handlePreferencesChange}
            >
              <option value="light">Light</option>
              <option value="dark">Dark</option>
            </select>
          </label>

          <label>
            Enable Notifications:
            <input
              type="checkbox"
              name="notifications"
              checked={preferences.notifications}
              onChange={handlePreferencesChange}
            />
          </label>
        </div>

        <button type="submit" disabled={isSubmitting}>
          {isSubmitting ? 'Saving...' : 'Save Changes'}
        </button>
      </form>
    </div>
  );
};

export default AccountSettings;
