import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Swal from 'sweetalert2';
import './VerifyOtp.css';

const VerifyOtp = () => {
  const [otp, setOtp] = useState('');
  const [error, setError] = useState('');
  const { email, password } = useLocation().state;
  const navigate = useNavigate();

  const handleChange = (e) => {
    setOtp(e.target.value);
  };

  const verifyOtp = async () => {
    if (!otp) {
      Swal.fire({
        icon: 'warning',
        title: 'Missing OTP',
        text: 'Please enter the OTP sent to your email.',
      });
      return;
    }

    try {
      const response = await axios.post('http://localhost:5001/auth/verifyOtp', {
        email,
        otp,
      });

      if (response.data.success) {
        // After OTP verification, submit all signup data
        await axios.post('http://localhost:5001/auth/signup', {
          email,
          password,
          ...response.data.userDetails, // Include other fields like name, phone, etc.
        });

        Swal.fire({
          icon: 'success',
          title: 'Signup Successful',
          text: 'You have successfully signed up!',
        });

        // Redirect to login or home page
        navigate('/login');
      } else {
        Swal.fire({
          icon: 'error',
          title: 'OTP Verification Failed',
          text: 'The OTP is incorrect or expired.',
        });
      }
    } catch (err) {
      setError(err.response ? err.response.data.message : 'Error verifying OTP.');
      Swal.fire({
        icon: 'error',
        title: 'Error',
        text: 'Error verifying OTP. Please try again.',
      });
    }
  };

  return (
    <div className="verify-otp">
      <h2>Verify OTP</h2>
      {error && <p className="error-message">{error}</p>}
      <input
        type="text"
        name="otp"
        placeholder="Enter OTP"
        value={otp}
        onChange={handleChange}
        required
      />
      <button onClick={verifyOtp}>Verify OTP</button>
    </div>
  );
};

export default VerifyOtp;
