import React, { useState } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './CreateBooking.css';

const CreateBooking = () => {
  const [formData, setFormData] = useState({
    customerName: '',
    venue: '',
    eventDate: null, // DatePicker uses null instead of an empty string
    eventType: '',
    numberOfGuests: '',
  });

  const [message, setMessage] = useState('');

  // List of auspicious dates (format: YYYY-MM-DD)
  const auspiciousDates = [
    '2024-01-15',
    '2024-02-10',
    '2024-03-05',
    '2024-04-20',
  ];

  // Function to check if a date is auspicious
  const isAuspiciousDate = (date) => {
    const formattedDate = date.toISOString().split('T')[0]; // Format date as YYYY-MM-DD
    return auspiciousDates.includes(formattedDate);
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleDateChange = (date) => {
    setFormData({
      ...formData,
      eventDate: date,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/bookings', {
        ...formData,
        eventDate: formData.eventDate ? formData.eventDate.toISOString().split('T')[0] : '',
      });
      setMessage(response.data.message);
      setFormData({
        customerName: '',
        venue: '',
        eventDate: null,
        eventType: '',
        numberOfGuests: '',
      });
    } catch (error) {
      setMessage('Failed to create booking.');
    }
  };

  return (
    <div className="create-booking">
      <h2>Create New Booking</h2>
      <form onSubmit={handleSubmit} className="booking-form">
        <div className="form-group">
          <label htmlFor="customerName">Customer Name</label>
          <input
            type="text"
            id="customerName"
            name="customerName"
            value={formData.customerName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="venue">Venue</label>
          <select
            id="venue"
            name="venue"
            value={formData.venue}
            onChange={handleChange}
            required
          >
            <option value="">Select Venue</option>
            <option value="Venue A">Venue A</option>
            <option value="Venue B">Venue B</option>
            <option value="Venue C">Venue C</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="eventDate">Event Date</label>
          <DatePicker
            selected={formData.eventDate}
            onChange={handleDateChange}
            filterDate={isAuspiciousDate} // Restrict to auspicious dates
            dateFormat="yyyy-MM-dd"
            placeholderText="Select an auspicious date"
            className="date-picker"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="eventType">Event Type</label>
          <select
            id="eventType"
            name="eventType"
            value={formData.eventType}
            onChange={handleChange}
            required
          >
            <option value="">Select Event Type</option>
            <option value="Wedding">Wedding</option>
            <option value="Conference">Conference</option>
            <option value="Birthday">Birthday</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="numberOfGuests">Number of Guests</label>
          <input
            type="number"
            id="numberOfGuests"
            name="numberOfGuests"
            value={formData.numberOfGuests}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit" className="submit-btn1">Create Booking</button>
      </form>

      {message && <div className="message">{message}</div>}
    </div>
  );
};

export default CreateBooking;
