import React, { useState } from 'react';
import { Formik, Field, Form } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import styled from 'styled-components';

// Styled Components
const Container = styled.div`
  width: 60%;
  margin: 50px auto;
  padding: 30px;
  background-color: #fff;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

const Heading = styled.h2`
  text-align: center;
  font-size: 32px;
  margin-bottom: 20px;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  margin-top: 6px;
  border-radius: 5px;
  border: 1px solid #ccc;
`;

const SubmitButton = styled.button`
  width: 100%;
  padding: 12px;
  background-color: #5f6368;
  color: white;
  border: none;
  border-radius: 4px;
  font-size: 16px;
  cursor: pointer;
`;

const ErrorMessage = styled.div`
  color: red;
  font-size: 14px;
`;

const FileUploadGroup = styled.div`
  margin-bottom: 15px;
`;

const Register = () => {
  const [upload, setUpload] = useState({
    marriageMahalProfile: null,
    businessRegistration: null,
    tradeLicense: null,
    foodLicense: null,
    fireSafetyCertificate: null,
    healthSanitationLicense: null,
    gstRegistration: null,
    liquorLicense: null,
    pestControlCertificate: null,
  });

  const initialValues = {
    email: '',
    password: '',
    confirmPassword: '',
    mahalName: '',
    mahalLocation: '',
    mahalCapacity: '',
  };

  const validationSchema = Yup.object({
    email: Yup.string().email('Invalid email address').required('Email is required'),
    password: Yup.string().min(6, 'Password must be at least 6 characters').required('Password is required'),
    confirmPassword: Yup.string()
      .oneOf([Yup.ref('password'), null], 'Passwords must match')
      .required('Confirm Password is required'),
    mahalName: Yup.string().required('Marriage Mahal Name is required'),
    mahalLocation: Yup.string().required('Marriage Mahal Location is required'),
    mahalCapacity: Yup.number().positive('Capacity must be a positive number').required('Capacity is required'),
  });

  const handleFileChange = (e, field) => {
    const file = e.target.files[0];
    setUpload((prev) => ({ ...prev, [field]: file }));
  };

  const handleRegisterSubmit = async (values, { setSubmitting, setStatus }) => {
    try {
      const formData = new FormData();
      formData.append('email', values.email);
      formData.append('password', values.password);
      formData.append('mahalName', values.mahalName);
      formData.append('mahalLocation', values.mahalLocation);
      formData.append('mahalCapacity', values.mahalCapacity);

      Object.keys(upload).forEach((field) => {
        if (upload[field]) {
          formData.append(field, upload[field]);
        }
      });

      const fileUploadResponse = await axios.post('http://localhost:5002/api/upload-file', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      if (fileUploadResponse.status === 200) {
        const fileId = fileUploadResponse.data.fileId;

        const registrationData = {
          email: values.email,
          password: values.password,
          mahalName: values.mahalName,
          mahalLocation: values.mahalLocation,
          mahalCapacity: values.mahalCapacity,
          fileId,
        };

        const registerResponse = await axios.post('http://localhost:5001/auth/register', registrationData);

        if (registerResponse.status === 201) {
          alert('Registration successful');
          window.location.reload();
        } else {
          setStatus({ error: 'Registration failed.' });
        }
      } else {
        setStatus({ error: 'File upload failed.' });
      }
    } catch (error) {
      console.error('Error:', error);
      setStatus({ error: 'An error occurred.' });
    }
  };

  return (
    <Container>
      <Heading>Register Marriage Mahal</Heading>

      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={handleRegisterSubmit}
      >
        {({ values, isSubmitting, status, touched, errors, setFieldValue }) => (
          <Form>
            {status?.error && <ErrorMessage>{status.error}</ErrorMessage>}

            {/* Owner Information */}
            <div>
              <h3>Owner Information</h3>
              <div>
                <Field name="email" type="email" placeholder="Owner Email" component={Input} />
                {touched.email && errors.email && <ErrorMessage>{errors.email}</ErrorMessage>}
              </div>
              <div>
                <Field name="password" type="password" placeholder="Password" component={Input} />
                {touched.password && errors.password && <ErrorMessage>{errors.password}</ErrorMessage>}
              </div>
              <div>
                <Field name="confirmPassword" type="password" placeholder="Confirm Password" component={Input} />
                {touched.confirmPassword && errors.confirmPassword && (
                  <ErrorMessage>{errors.confirmPassword}</ErrorMessage>
                )}
              </div>
            </div>

            {/* Marriage Mahal Information */}
            <div>
              <Field name="mahalName" type="text" placeholder="Marriage Mahal Name" component={Input} />
              {touched.mahalName && errors.mahalName && <ErrorMessage>{errors.mahalName}</ErrorMessage>}
              <Field name="mahalLocation" type="text" placeholder="Marriage Mahal Location" component={Input} />
              {touched.mahalLocation && errors.mahalLocation && (
                <ErrorMessage>{errors.mahalLocation}</ErrorMessage>
              )}
              <Field name="mahalCapacity" type="number" placeholder="Marriage Mahal Capacity" component={Input} />
              {touched.mahalCapacity && errors.mahalCapacity && <ErrorMessage>{errors.mahalCapacity}</ErrorMessage>}

              {/* File Upload for Marriage Mahal Profile */}
              <FileUploadGroup>
                <label>Marriage Mahal Profile:</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'marriageMahalProfile')}
                />
                {upload.marriageMahalProfile && (
                  <p>File selected: {upload.marriageMahalProfile.name}</p>
                )}
              </FileUploadGroup>

              {/* File Upload for required certificates */}
              {[
                'tradeLicense',
                'foodLicense',
                'fireSafetyCertificate',
                'healthSanitationLicense',
                'gstRegistration',
                'liquorLicense',
                'pestControlCertificate',
              ].map((field) => (
                <FileUploadGroup key={field}>
                  <label>{field.replace(/([A-Z])/g, ' $1').toUpperCase()}:</label>
                  <input type="file" onChange={(e) => handleFileChange(e, field)} />
                </FileUploadGroup>
              ))}

              <SubmitButton type="submit" disabled={isSubmitting}>
                Submit
              </SubmitButton>
            </div>
          </Form>
        )}
      </Formik>
    </Container>
  );
};

export default Register;
