import React, { useState } from 'react';
import { Formik, Field, Form } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';

// Custom input component
const InputField = ({ field, form, ...props }) => {
  return (
    <div>
      <input {...field} {...props} />
      {form.touched[field.name] && form.errors[field.name] && (
        <div style={{ color: 'red' }}>{form.errors[field.name]}</div>
      )}
    </div>
  );
};

const Register = () => {
  const [upload, setUpload] = useState({
    marriageMahalProfile: null,
    businessRegistration: null,
    tradeLicense: null,
    foodLicense: null,
    fireSafetyCertificate: null,
    healthSanitationLicense: null,
    gstRegistration: null,
    liquorLicense: null,
    pestControlCertificate: null,
  });

  const initialValues = {
    email: '',
    password: '',
    confirmPassword: '',
    mahalName: '',
    mahalLocation: '',
    mahalCapacity: '',
  };

  const validationSchema = Yup.object({
    email: Yup.string().email('Invalid email address').required('Email is required'),
    password: Yup.string().min(6, 'Password must be at least 6 characters').required('Password is required'),
    confirmPassword: Yup.string()
      .oneOf([Yup.ref('password'), null], 'Passwords must match')
      .required('Confirm Password is required'),
    mahalName: Yup.string().required('Marriage Mahal Name is required'),
    mahalLocation: Yup.string().required('Marriage Mahal Location is required'),
    mahalCapacity: Yup.number().positive('Capacity must be a positive number').required('Capacity is required'),
  });

  const handleFileChange = (e, field) => {
    const file = e.target.files[0];
    setUpload((prev) => ({ ...prev, [field]: file }));
  };

  // Separate function to handle user data (email, password, etc.)
  const handleSubmit = async (values, { setSubmitting, setStatus }) => {
    setSubmitting(true);

    try {
      // Prepare files for upload
      const formData = new FormData();
      for (let key in upload) {
        if (upload[key]) {
          formData.append(key, upload[key]);
        }
      }

      // Handle file upload
      if (formData.has('marriageMahalProfile') || formData.has('businessRegistration')) {
        const fileResponse = await axios.post('http://localhost:5000/api/owners/upload-files', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });
        setStatus({ success: 'Files uploaded successfully!' });
      }

      // Handle user registration
      const userResponse = await axios.post('http://localhost:5000/auth/register', values);
      setStatus({ success: 'User Registration Successful!' });
    } catch (error) {
      const message = error.response?.data?.error || 'An error occurred. Please try again.';
      setStatus({ error: message });
    }

    setSubmitting(false);
  };

  return (
    <div>
      <h2>Register Marriage Mahal</h2>

      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={handleSubmit}
      >
        {({ values, isSubmitting, status }) => (
          <Form>
            {status?.error && <p style={{ color: 'red' }}>{status.error}</p>}
            {status?.success && <p style={{ color: 'green' }}>{status.success}</p>}

            {/* Owner Information */}
            <div>
              <h3>Owner Information</h3>
              <Field
                name="email"
                type="email"
                placeholder="Owner Email"
                component={InputField}
              />
              <Field
                name="password"
                type="password"
                placeholder="Password"
                component={InputField}
              />
              <Field
                name="confirmPassword"
                type="password"
                placeholder="Confirm Password"
                component={InputField}
              />
            </div>

            {/* Marriage Mahal Information */}
            <div>
              <Field
                name="mahalName"
                type="text"
                placeholder="Marriage Mahal Name"
                component={InputField}
              />
              <Field
                name="mahalLocation"
                type="text"
                placeholder="Marriage Mahal Location"
                component={InputField}
              />
              <Field
                name="mahalCapacity"
                type="number"
                placeholder="Marriage Mahal Capacity"
                component={InputField}
              />

              {/* File Upload for Marriage Mahal Profile */}
              <div>
                <label>Marriage Mahal Profile:</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'marriageMahalProfile')}
                />
                {upload.marriageMahalProfile && (
                  <p>File selected: {upload.marriageMahalProfile.name}</p>
                )}
              </div>

              {/* File Upload for required certificates */}
              <div>
                <label>Business Registration:</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'businessRegistration')}
                />
              </div>
              <div>
                <label>Trade License:</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'tradeLicense')}
                />
              </div>
              <div>
                <label>Food License (if applicable):</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'foodLicense')}
                />
              </div>
              <div>
                <label>Fire Safety Certificate:</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'fireSafetyCertificate')}
                />
              </div>
              <div>
                <label>Health and Sanitation License:</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'healthSanitationLicense')}
                />
              </div>
              <div>
                <label>GST Registration:</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'gstRegistration')}
                />
              </div>
              <div>
                <label>Liquor License (if applicable):</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'liquorLicense')}
                />
              </div>
              <div>
                <label>Pest Control Certification:</label>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, 'pestControlCertificate')}
                />
              </div>

              <button type="submit" disabled={isSubmitting}>
                Submit
              </button>
            </div>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default Register;
