import React, { useState } from 'react';
import axios from 'axios';
import Modal from './Modal'; // Import the Modal component

const SignupForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    password: '',
    otp: '',
  });
  const [isOtpSent, setIsOtpSent] = useState(false);
  const [error, setError] = useState('');
  const [generatedOtp, setGeneratedOtp] = useState('');
  const [modalMessage, setModalMessage] = useState('');
  const [isModalVisible, setIsModalVisible] = useState(false);

  // Handle form input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.otp === generatedOtp) {
      try {
        const response = await axios.post('http://localhost:5001/auth/signup', formData);
        setModalMessage('Signup successful!');
        setIsModalVisible(true);
      } catch (err) {
        setModalMessage(err.response ? err.response.data.message : 'Error occurred during signup.');
        setIsModalVisible(true);
      }
    } else {
      setModalMessage('Invalid OTP!');
      setIsModalVisible(true);
    }
  };

  // Handle OTP generation and sending
  const sendOtp = async () => {
    try {
      const response = await axios.post('http://localhost:5001/auth/sendOtp', { email: formData.email });
      setGeneratedOtp(response.data.otp);  // Store the OTP sent from the backend
      setIsOtpSent(true);
      setError(''); // Reset error after sending OTP
    } catch (err) {
      setModalMessage(err.response ? err.response.data.message : 'Error sending OTP.');
      setIsModalVisible(true);
    }
  };

  const closeModal = () => {
    setIsModalVisible(false);
  };

  return (
    <div>
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Full Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email Address"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="tel"
          name="phone"
          placeholder="Phone Number"
          value={formData.phone}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="address"
          placeholder="Address"
          value={formData.address}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
        />
        {isOtpSent ? (
          <>
            <input
              type="text"
              name="otp"
              placeholder="Enter OTP"
              value={formData.otp}
              onChange={handleChange}
              required
            />
            <button type="submit">Verify OTP and Sign Up</button>
          </>
        ) : (
          <button type="button" onClick={sendOtp}>
            Send OTP
          </button>
        )}
      </form>

      {isModalVisible && <Modal message={modalMessage} onClose={closeModal} />}
    </div>
  );
};

export default SignupForm;
