import React, { useState } from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';

// Custom input component
const InputField = ({ field, form, ...props }) => {
  return (
    <div>
      <input {...field} {...props} />
      {form.touched[field.name] && form.errors[field.name] && (
        <div style={{ color: 'red' }}>{form.errors[field.name]}</div>
      )}
    </div>
  );
};

const Register = () => {
  const [step, setStep] = useState(1);
  const [upload, setUpload] = useState(null); // State for uploaded file

  // Initial form values
  const initialValues = {
    name: '',
    email: '',
    password: '',
    profilePicture: null,
    maritalStatus: '', // Determines conditional field
    mahalName: '',
    mahalLocation: '',
    mahalCapacity: '',
  };

  // Step 1: Basic user info validation
  const validationSchemaStep1 = Yup.object({
    name: Yup.string().required('Name is required'),
    email: Yup.string().email('Invalid email address').required('Email is required'),
    password: Yup.string()
      .min(6, 'Password must be at least 6 characters')
      .required('Password is required'),
  });

  // Step 2: Marriage Mahal details validation
  const validationSchemaStep2 = Yup.object({
    mahalName: Yup.string().when('maritalStatus', {
      is: 'married',
      then: Yup.string().required('Marriage Mahal Name is required'),
    }),
    mahalLocation: Yup.string().when('maritalStatus', {
      is: 'married',
      then: Yup.string().required('Marriage Mahal Location is required'),
    }),
    mahalCapacity: Yup.number()
      .when('maritalStatus', {
        is: 'married',
        then: Yup.number().positive('Capacity must be a positive number').required('Capacity is required'),
      }),
  });

  // Submit handler
  const handleSubmit = async (values, { setSubmitting, setStatus }) => {
    try {
      const response = await axios.post('http://localhost:5000/api/owners/register', values);
      setStatus({ success: 'Registration successful. You can now log in!' });
    } catch (error) {
      const message = error.response?.data?.error || 'Registration failed. Please try again.';
      setStatus({ error: message });
    }
    setSubmitting(false);
  };

  // Next Step Handler
  const nextStep = () => setStep((prev) => Math.min(prev + 1, 2));

  // Previous Step Handler
  const prevStep = () => setStep((prev) => Math.max(prev - 1, 1));

  return (
    <div>
      <h2>Register</h2>
      <ProgressBar step={step} />

      <Formik
        initialValues={initialValues}
        validationSchema={step === 1 ? validationSchemaStep1 : validationSchemaStep2}
        onSubmit={handleSubmit}
      >
        {({ values, setFieldValue, isSubmitting, status }) => (
          <Form>
            {status?.error && <p style={{ color: 'red' }}>{status.error}</p>}
            {status?.success && <p style={{ color: 'green' }}>{status.success}</p>}

            {/* Step 1: Basic Info */}
            {step === 1 && (
              <div>
                <h3>Basic Information</h3>
                <Field
                  name="name"
                  type="text"
                  placeholder="Name"
                  component={InputField}
                />
                <Field
                  name="email"
                  type="email"
                  placeholder="Email"
                  component={InputField}
                />
                <Field
                  name="password"
                  type="password"
                  placeholder="Password"
                  component={InputField}
                />

                {/* File Upload */}
                <input
                  type="file"
                  onChange={(e) => setUpload(e.target.files[0])}
                />
                {upload && <p>File selected: {upload.name}</p>}

                <button type="button" onClick={nextStep} disabled={isSubmitting}>
                  Next
                </button>
              </div>
            )}

            {/* Step 2: Marriage Mahal Info */}
            {step === 2 && (
              <div>
                <h3>Marriage Mahal Information</h3>
                <div>
                  <label>Marital Status</label>
                  <Field as="select" name="maritalStatus">
                    <option value="">Select Status</option>
                    <option value="single">Single</option>
                    <option value="married">Married</option>
                  </Field>
                </div>

                {values.maritalStatus === 'married' && (
                  <>
                    <Field
                      name="mahalName"
                      type="text"
                      placeholder="Marriage Mahal Name"
                      component={InputField}
                    />
                    <Field
                      name="mahalLocation"
                      type="text"
                      placeholder="Marriage Mahal Location"
                      component={InputField}
                    />
                    <Field
                      name="mahalCapacity"
                      type="number"
                      placeholder="Marriage Mahal Capacity"
                      component={InputField}
                    />
                  </>
                )}

                <button type="button" onClick={prevStep} disabled={isSubmitting}>
                  Back
                </button>
                <button type="submit" disabled={isSubmitting}>
                  Submit
                </button>
              </div>
            )}
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default Register;
