import React, { useState, useEffect } from 'react';
import './ViewBookings.css';

const ViewBookings = () => {
  const [bookings, setBookings] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  // Simulated API call to fetch bookings
  useEffect(() => {
    const fetchBookings = async () => {
      setIsLoading(true);

      // Simulating a delay and fetching data
      const simulatedBookings = [
        {
          id: 1,
          customerName: 'John Doe',
          venue: 'Venue A',
          eventDate: '2024-12-25',
          eventType: 'Wedding',
          numberOfGuests: 200,
        },
        {
          id: 2,
          customerName: 'Jane Smith',
          venue: 'Venue B',
          eventDate: '2024-12-31',
          eventType: 'Conference',
          numberOfGuests: 50,
        },
      ];

      // Simulate API call delay
      setTimeout(() => {
        setBookings(simulatedBookings);
        setIsLoading(false);
      }, 1000);
    };

    fetchBookings();
  }, []);

  return (
    <div className="view-bookings">
      <h2>Your Bookings</h2>
      {isLoading ? (
        <p>Loading bookings...</p>
      ) : bookings.length > 0 ? (
        <div className="bookings-list">
          {bookings.map((booking) => (
            <div key={booking.id} className="booking-card">
              <h3>{booking.venue}</h3>
              <p>
                <strong>Customer:</strong> {booking.customerName}
              </p>
              <p>
                <strong>Event Date:</strong> {booking.eventDate}
              </p>
              <p>
                <strong>Event Type:</strong> {booking.eventType}
              </p>
              <p>
                <strong>Guests:</strong> {booking.numberOfGuests}
              </p>
              <button>Edit</button>
            </div>
          ))}
        </div>
      ) : (
        <p>No bookings found.</p>
      )}
    </div>
  );
};

export default ViewBookings;
