import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import axios from 'axios';

const SignupForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    password: '',
  });
  const [error, setError] = useState('');
  const navigate = useNavigate(); // Initialize navigate

  // Handle form input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle OTP generation and sending
  const sendOtp = async () => {
    if (!formData.email) {
      alert('Please enter your email address before requesting an OTP.');
      return;
    }
    try {
      const response = await axios.post('http://localhost:5001/auth/sendOtp', { email: formData.email });
      alert('OTP sent to your email address!');
      navigate('/verify-otp', { state: { email: formData.email, otp: response.data.otp } }); // Navigate to OTP page
    } catch (err) {
      setError(err.response ? err.response.data.message : 'Error sending OTP.');
      alert('Error sending OTP. Please try again.');
    }
  };

  return (
    <div>
      <h2>Sign Up</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form>
        <input
          type="text"
          name="name"
          placeholder="Full Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email Address"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="tel"
          name="phone"
          placeholder="Phone Number"
          value={formData.phone}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="address"
          placeholder="Address"
          value={formData.address}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
        />
        <button type="button" onClick={sendOtp}>
          Send OTP
        </button>
      </form>
    </div>
  );
};

export default SignupForm;
