import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Card, Form, Input, Checkbox, Table, Modal, notification } from 'antd';
import { CalendarOutlined, UploadOutlined } from '@ant-design/icons';
import 'antd/dist/antd.css';

const OwnerDashboard = () => {
  const [image, setImage] = useState(null);
  const [imageUrl, setImageUrl] = useState('');
  const [slots, setSlots] = useState([]);
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [available, setAvailable] = useState(true);
  const [bookings, setBookings] = useState([]);
  const [showModal, setShowModal] = useState(false);

  // Fetch existing slots and bookings when the component mounts
  useEffect(() => {
    axios.get('http://localhost:5000/api/slots')
      .then(response => setSlots(response.data))
      .catch(error => console.error('Error fetching slots:', error));

    axios.get('http://localhost:5000/api/bookings')
      .then(response => setBookings(response.data))
      .catch(error => console.error('Error fetching bookings:', error));
  }, []);

  // Handle image upload
  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleImageUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('mahalImage', image);

    try {
      const response = await axios.post('http://localhost:5000/api/mahal-images', formData);
      setImageUrl(response.data.imageUrl);
      notification.success({ message: 'Image uploaded successfully' });
    } catch (error) {
      console.error('Error uploading image:', error);
      notification.error({ message: 'Error uploading image' });
    }
  };

  // Handle slot creation
  const handleAddSlot = async (e) => {
    e.preventDefault();
    const newSlot = { date, time, available };

    try {
      await axios.post('http://localhost:5000/api/slots', newSlot);
      notification.success({ message: 'Slot added successfully' });
      setSlots([...slots, newSlot]); // Update slots in state
    } catch (error) {
      console.error('Error adding slot:', error);
      notification.error({ message: 'Error adding slot' });
    }
  };

  // Handle booking acceptance or rejection
  const handleBookingStatusChange = (bookingId, status) => {
    axios.put(`http://localhost:5000/api/bookings/${bookingId}`, { status })
      .then(() => {
        setBookings(bookings.map(booking =>
          booking._id === bookingId ? { ...booking, status } : booking
        ));
        notification.success({ message: `Booking ${status}` });
      })
      .catch(error => {
        console.error('Error updating booking status:', error);
        notification.error({ message: 'Error updating booking status' });
      });
  };

  return (
    <div className="dashboard">
      <h1>Marriage Mahal Owner Dashboard</h1>

      {/* Sidebar */}
      <div className="sidebar">
        <Button icon={<CalendarOutlined />} onClick={() => setShowModal(true)}>
          View Calendar
        </Button>
      </div>

      {/* Mahal Image Upload */}
      <Card title="Upload Mahal Image" className="card">
        <Form onSubmit={handleImageUpload}>
          <Form.Item>
            <Input type="file" onChange={handleImageChange} required />
          </Form.Item>
          <Button type="primary" icon={<UploadOutlined />} onClick={handleImageUpload}>
            Upload Image
          </Button>
        </Form>
        {imageUrl && <img src={`http://localhost:5000${imageUrl}`} alt="Mahal" width="300" />}
      </Card>

      {/* Slot Management */}
      <Card title="Manage Slots" className="card">
        <Form onSubmit={handleAddSlot}>
          <Form.Item label="Date">
            <Input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
          </Form.Item>
          <Form.Item label="Time">
            <Input type="time" value={time} onChange={(e) => setTime(e.target.value)} required />
          </Form.Item>
          <Form.Item label="Available">
            <Checkbox checked={available} onChange={(e) => setAvailable(e.target.checked)} />
          </Form.Item>
          <Button type="primary" onClick={handleAddSlot}>
            Add Slot
          </Button>
        </Form>

        <h3>Available Slots</h3>
        <Table dataSource={slots} columns={[
          { title: 'Date', dataIndex: 'date', render: date => new Date(date).toLocaleDateString() },
          { title: 'Time', dataIndex: 'time' },
          { title: 'Availability', dataIndex: 'available', render: available => available ? 'Available' : 'Not Available' }
        ]} />
      </Card>

      {/* Booking Management */}
      <Card title="Manage Bookings" className="card">
        <Table dataSource={bookings} columns={[
          { title: 'Customer Name', dataIndex: 'customerName' },
          { title: 'Date', dataIndex: 'date', render: date => new Date(date).toLocaleDateString() },
          { title: 'Time', dataIndex: 'time' },
          { title: 'Status', dataIndex: 'status' },
          {
            title: 'Action',
            render: (text, record) => (
              <>
                <Button onClick={() => handleBookingStatusChange(record._id, 'Accepted')}>Accept</Button>
                <Button onClick={() => handleBookingStatusChange(record._id, 'Rejected')}>Reject</Button>
              </>
            ),
          },
        ]} />
      </Card>

      {/* Calendar Modal */}
      <Modal
        title="Calendar View"
        visible={showModal}
        onCancel={() => setShowModal(false)}
        footer={null}
      >
        {/* Calendar component or library */}
        <p>Calendar will be integrated here.</p>
      </Modal>
    </div>
  );
};

export default OwnerDashboard;
