// SignupForm.js
import React, { useState } from 'react';
import axios from 'axios';

const SignupForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    password: '',
    otp: '',
    generatedOtp: '',
  });
  const [isOtpSent, setIsOtpSent] = useState(false);
  const [error, setError] = useState('');

  // Handle form input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.otp === formData.generatedOtp) {
      try {
        const response = await axios.post('http://localhost:5000/auth/signup', formData);
        alert('Signup successful!');
        // Redirect or handle the response as needed
      } catch (err) {
        setError(err.response.data.message);
      }
    } else {
      setError('Invalid OTP!');
    }
  };

  // Handle OTP generation and sending
  const sendOtp = async () => {
    try {
      const response = await axios.post('http://localhost:5001/auth/sendOtp', { email: formData.email });
      setFormData({ ...formData, generatedOtp: response.data.otp });
      setIsOtpSent(true);
    } catch (err) {
      setError(err.response.data.message);
    }
  };

  return (
    <div>
      <h2>Sign Up</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Full Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email Address"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="tel"
          name="phone"
          placeholder="Phone Number"
          value={formData.phone}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="address"
          placeholder="Address"
          value={formData.address}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
        />
        {isOtpSent ? (
          <>
            <input
              type="text"
              name="otp"
              placeholder="Enter OTP"
              value={formData.otp}
              onChange={handleChange}
              required
            />
            <button type="submit">Verify OTP and Sign Up</button>
          </>
        ) : (
          <button type="button" onClick={sendOtp}>
            Send OTP
          </button>
        )}
      </form>
    </div>
  );
};

export default SignupForm;
