import React, { useState } from 'react';
import './Settings.css';

const Settings = () => {
  const [userDetails, setUserDetails] = useState({
    name: 'John Doe',
    email: 'johndoe@example.com',
    password: '',
    preferences: 'Email Notifications',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserDetails((prevDetails) => ({
      ...prevDetails,
      [name]: value,
    }));
  };

  const handleSave = () => {
    // Simulate saving the details
    alert('Settings saved successfully!');
  };

  return (
    <div className="settings-page">
      <h2>Account Settings</h2>
      <form className="settings-form">
        <div className="form-group">
          <label htmlFor="name">Full Name</label>
          <input
            type="text"
            id="name"
            name="name"
            value={userDetails.name}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={userDetails.email}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={userDetails.password}
            placeholder="Enter new password"
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="preferences">Preferences</label>
          <select
            id="preferences"
            name="preferences"
            value={userDetails.preferences}
            onChange={handleChange}
          >
            <option value="Email Notifications">Email Notifications</option>
            <option value="SMS Notifications">SMS Notifications</option>
            <option value="Push Notifications">Push Notifications</option>
          </select>
        </div>
        <button type="button" className="save-button" onClick={handleSave}>
          Save Changes
        </button>
      </form>
    </div>
  );
};

export default Settings;
