import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './CreateBooking.css';

const CreateBooking = () => {
  const [formData, setFormData] = useState({
    customerName: '',
    venue: '',
    eventDate: '',
    eventType: '',
    numberOfGuests: '',
  });

  const [availableDates, setAvailableDates] = useState([]);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleVenueChange = async (e) => {
    const selectedVenue = e.target.value;
    setFormData({
      ...formData,
      venue: selectedVenue,
      eventDate: '', // Reset event date when venue changes
    });

    // Fetch available dates based on selected venue
    if (selectedVenue) {
      try {
        const response = await axios.get(`http://localhost:5000/api/available-dates/${selectedVenue}`);
        setAvailableDates(response.data.dates);
      } catch (error) {
        setMessage('Failed to fetch available dates.');
      }
    } else {
      setAvailableDates([]); // Clear available dates if no venue is selected
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send form data to the backend
      const response = await axios.post('http://localhost:5000/api/bookings', formData);
      setMessage(response.data.message);

      // Clear form fields after successful submission
      setFormData({
        customerName: '',
        venue: '',
        eventDate: '',
        eventType: '',
        numberOfGuests: '',
      });
      setAvailableDates([]); // Clear available dates after submission
    } catch (error) {
      setMessage('Failed to create booking.');
    }
  };

  return (
    <div className="create-booking">
      <h2>Create New Booking</h2>
      <form onSubmit={handleSubmit} className="booking-form">
        <div className="form-group">
          <label htmlFor="customerName">Customer Name</label>
          <input
            type="text"
            id="customerName"
            name="customerName"
            value={formData.customerName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="venue">Venue</label>
          <select
            id="venue"
            name="venue"
            value={formData.venue}
            onChange={handleVenueChange}
            required
          >
            <option value="">Select Venue</option>
            <option value="Venue A">Venue A</option>
            <option value="Venue B">Venue B</option>
            <option value="Venue C">Venue C</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="eventDate">Event Date</label>
          <select
            id="eventDate"
            name="eventDate"
            value={formData.eventDate}
            onChange={handleChange}
            required
            disabled={availableDates.length === 0}
          >
            <option value="">Select Event Date</option>
            {availableDates.map((date, index) => (
              <option key={index} value={date}>
                {date}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="eventType">Event Type</label>
          <select
            id="eventType"
            name="eventType"
            value={formData.eventType}
            onChange={handleChange}
            required
          >
            <option value="">Select Event Type</option>
            <option value="Wedding">Wedding</option>
            <option value="Conference">Conference</option>
            <option value="Birthday">Birthday</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="numberOfGuests">Number of Guests</label>
          <input
            type="number"
            id="numberOfGuests"
            name="numberOfGuests"
            value={formData.numberOfGuests}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit" className="submit-btn">Create Booking</button>
      </form>

      {message && <div className="message">{message}</div>}
    </div>
  );
};

export default CreateBooking;
