import React, { useState } from "react";
import axios from "axios";
import "./CreateBooking.css";

const AdvancedBookingForm = () => {
  const [formData, setFormData] = useState({
    customerName: "",
    email: "",
    phone: "",
    location: "",
    venue: "",
    eventDate: "",
    eventType: "",
    numberOfGuests: "",
    specialRequests: "",
    guestDetails: [{ name: "", preference: "" }],
    attachment: null,
  });

  const [nearbyMahals, setNearbyMahals] = useState([]);
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    const { name, value, type, files } = e.target;
    if (type === "file") {
      setFormData({ ...formData, attachment: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const fetchNearbyMahals = async () => {
    try {
      // Replace with your backend endpoint or Google Places API
      const response = await axios.get(
        `http://localhost:5000/api/nearby-mahals?location=${formData.location}`
      );
      setNearbyMahals(response.data.mahals);
    } catch (error) {
      setMessage("Failed to fetch nearby mahals.");
    }
  };

  const handleLocationChange = (e) => {
    const location = e.target.value;
    setFormData({ ...formData, location });
    if (location) {
      fetchNearbyMahals(); // Fetch mahals when location is updated
    }
  };

  const handleGuestChange = (index, e) => {
    const updatedGuests = [...formData.guestDetails];
    updatedGuests[index][e.target.name] = e.target.value;
    setFormData({ ...formData, guestDetails: updatedGuests });
  };

  const addGuest = () => {
    setFormData({
      ...formData,
      guestDetails: [...formData.guestDetails, { name: "", preference: "" }],
    });
  };

  const removeGuest = (index) => {
    const updatedGuests = formData.guestDetails.filter((_, i) => i !== index);
    setFormData({ ...formData, guestDetails: updatedGuests });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formDataToSend = new FormData();
    Object.entries(formData).forEach(([key, value]) => {
      if (key === "guestDetails") {
        formDataToSend.append(key, JSON.stringify(value));
      } else {
        formDataToSend.append(key, value);
      }
    });

    try {
      const response = await axios.post(
        "http://localhost:5000/api/bookings",
        formDataToSend
      );
      setMessage(response.data.message);
      setFormData({
        customerName: "",
        email: "",
        phone: "",
        location: "",
        venue: "",
        eventDate: "",
        eventType: "",
        numberOfGuests: "",
        specialRequests: "",
        guestDetails: [{ name: "", preference: "" }],
        attachment: null,
      });
      setNearbyMahals([]);
    } catch (error) {
      setMessage("Failed to create booking.");
    }
  };

  return (
    <div className="create-booking">
      <h2>Mahal Booking</h2>
      <form onSubmit={handleSubmit} className="booking-form" encType="multipart/form-data">
        <div className="form-group">
          <label htmlFor="customerName">Customer Name</label>
          <input
            type="text"
            id="customerName"
            name="customerName"
            value={formData.customerName}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="phone">Phone</label>
          <input
            type="tel"
            id="phone"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="location">Location</label>
          <input
            type="text"
            id="location"
            name="location"
            placeholder="Enter your location"
            value={formData.location}
            onChange={handleLocationChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="venue">Venue</label>
          <select
            id="venue"
            name="venue"
            value={formData.venue}
            onChange={handleChange}
            required
          >
            <option value="">Select Venue</option>
            {nearbyMahals.map((mahal, index) => (
              <option key={index} value={mahal.name}>
                {mahal.name} ({mahal.distance} km)
              </option>
            ))}
          </select>
        </div>

        {/* Other fields remain unchanged */}
        {/* Additional fields for event details */}
        <button type="submit" className="submit-btn">Create Booking</button>
      </form>

      {message && <div className="message">{message}</div>}
    </div>
  );
};

export default AdvancedBookingForm;
